mapper.insert:
    	Insert INTO Netflix_db.Location(ZipCode, City, State) 
			SELECT * FROM (SELECT  #{zip}, #{city}, #{state}) AS tmp
			WHERE NOT EXISTS(
					SELECT ZipCode FROM Location WHERE  ZipCode= #{zip}
			) LIMIT 1;

mapper.update:
  	UPDATE Customer
  	
  	 CreditCardNumber = #{customer.creditCardNumber},
  	 Password = #{customer.password}
  	
		WHERE Id = #{customer.SSN};

    	UPDATE Person
    	
    	 Address = #{customer.address},
	   	 Telephone = #{customer.telephone},
    	 ZipCode = #{customer.zipCode}
    	
  		WHERE SSN = #{customer.SSN};
